option.value:
```typescript jsx
'use client';

import {
  ContentContainer,
  RHFCheckboxField,
  RHForm,
  RHFRadioField,
  RHFSelectField,
  RHFSubmitButton,
  RHFTextField,
} from '@/components';
import { Box, Typography } from '@/ui';

import { styles } from './CustomerEditForm.css';
import React from 'react';
import { FormProvider, useForm, useFormContext } from 'react-hook-form';
import { isNumber, maxValueOption, minValueOption, requiredOption } from '@/lib';

export function CustomerEditForm(payload: any) {

  // const { register } = useFormContext();
  const methods = useForm();
  const register = useFormContext();

  const defaultKeyName = ['id', 'email', 'created_at', 'updated_at', 'deleted_at'];
  const parsedUser = {
    defaultKeys: [] as any[],
    customKeys: [] as any[],
  };

  payload.payload.forEach((item) => {
    const target = defaultKeyName.includes(item.key_name) ? 'defaultKeys' : 'customKeys';
    parsedUser[target].push(item);
  });

  const getDelimiterChar = (delimiterName) => {
    switch (delimiterName) {
      case 'hyphen':
        return '－';  // 全角ハイフンに変更
      case 'space':
        return '　';
      case 'slash':
        return '／';
      default:
        return '';
    }
  };

// 使用するデリミタに基づいてフィールドの値を分割する関数
  const splitFieldValue = (item) => {
    return item.split_count > 1
      ? typeof item.value === "string"
        ? item.value.split(getDelimiterChar(item.delimiter.name))
        : []
      : [item.value];
  };

// 新しいフィールド名を作成する関数
  const createFieldName = (item, index) => {
    return item.split_count > 1 ? `${item.key_name}_${index}` : item.key_name;
  };

// 各フィールドを収容するためのdefaultValuesオブジェクトを作成する関数
  const createDefaultValues = (fields) => {
    const updatedFields = fields.flatMap(item => {
      return splitFieldValue(item).map((val, index) => {
        const key = createFieldName(item, index);
        return { [key]: typeof val === "string" ? val.trim() : val };
      });
    });

    const defaultValues = updatedFields.reduce((accumulator, field) => {
      return { ...accumulator, ...field };
    }, {});

    return defaultValues;
  };

// formPropsオブジェクト
  const formProps = {
    defaultValues: createDefaultValues(parsedUser.customKeys),
  };

  const handleSubmit = (formData) => {
    const getKeyValues = (keyItem, formData) => {
      const values = Array.from({length: keyItem.split_count}, (_, i) => {
        const key = `${keyItem.key_name}_${i}`;
        const value = typeof formData[key] === "string" ? formData[key].trim() : formData[key];
        delete formData[key];
        return value;
      });

      return values.join(getDelimiterChar(keyItem.delimiter.name));
    }

    const mergedData = parsedUser.customKeys.reduce((mergedDataAccumulator, keyItem) => {
      const value = keyItem.split_count > 1
        ? getKeyValues(keyItem, formData)
        : formData[keyItem.key_name];

      mergedDataAccumulator[keyItem.key_name] = value;

      return mergedDataAccumulator;
    }, {});

    console.log(mergedData);
  };

  const getDelimiterCharElement = (delimiterName) => <span>{getDelimiterChar(delimiterName)}</span>;

  return (
    <FormProvider {...methods}>
    <ContentContainer>
      <ul>
        <RHForm formProps={formProps} onSubmit={handleSubmit}>
          {parsedUser.customKeys.map((keyItem) => {
            const options = keyItem.items?.map((item: any) => ({
              id: item.id,
              name: item.value,
              value: item.value,
            }));

            const checkboxField = keyItem.selector?.name === 'checkbox' && (
              <Box>
                <RHFCheckboxField
                  id={keyItem.key_name}
                  name={keyItem.key_name}
                  data={options}
                />
              </Box>
            );

            const radioField = keyItem.selector?.name === 'radio' && (
              <RHFRadioField
                id={keyItem.key_name}
                name={keyItem.key_name}
                width="lg"
                data={options}
              />
            );

            const selectField = keyItem.selector?.name === 'selectbox' && (
              <RHFSelectField
                id={keyItem.key_name}
                name={keyItem.key_name}
                width="lg"
                selectOptions={options}
              />
            );

            const splitTextFields = keyItem.split_count > 1 &&
              <div style={{ display: 'flex' }}>
                {Array(keyItem.split_count)
                  .fill(null)
                  .map((_, index) => (
                    <React.Fragment key={`${keyItem.key_name}_${index}`}>
                      <RHFTextField
                        id={`${keyItem.key_name}_${index}`}
                        name={`${keyItem.key_name}_${index}`}
                        width="sm"
                      />
                      {(index !== keyItem.split_count - 1) && getDelimiterCharElement(keyItem.delimiter.name)}
                    </React.Fragment>
                  ))}
              </div>;

            const defaultField =
              <RHFTextField
                id={keyItem.key_name}
                name={keyItem.key_name}
                width="lg"
                options={{
                  // valueAsNumber: true,
                  validate: (value, formValues) => {
                    // Numberを絞り込むがこの文言が表示されることはない、引数のvalueにnumberを与えると返却値に対して型エラーが起きるのでこのような処理にしている
                    if (!isNumber(value)) return '数字でない値は設定できません';
                    return value > formValues.maxValue ? true : '最大値より大きい値は設定できません';
                  },
                  min: keyItem.min_value,
                  max: keyItem.max_value,
                }}
              />;

            return (
              <li key={keyItem.key_name} className={styles.Item}>
                <Typography as="span">{keyItem.key_name}</Typography>
                {selectField || checkboxField || radioField ||splitTextFields || defaultField}
              </li>
            );
          })}

          <RHFSubmitButton submittingText="データを保存中です">変更内容を保存</RHFSubmitButton>
        </RHForm>
      </ul>
    </ContentContainer>
    </FormProvider>
  );
}

```
`RHFTextField`にバリデーションチェックを実装していますが、全ての`defaultField`が反応してしまいます。

